// Stops the program execution
// Zatrzymuje działanie programu
ROZKAZ STP;
ARGUMENTY 0;
czyt wys wei il;
stop;

// Adds value from Memory[AD] to AK
// Dodaje wartość z Pemięci[AD] do AK
ROZKAZ DOD;
czyt wys wei il;
wyad wea eni
JEzELI INT TO @przerw GDY NIE @norm;
@norm czyt wys weja dod weak wyl wea KONIEC;
@przerw czyt wys weja dod weak dws;
wyls wes wyws wea;
pisz;
wyap wel wea rint;

// Substracts value from Memory[AD] to AK
// Odejmuje wartość z Pemięci[AD] do AK
ROZKAZ ODE;
czyt wys wei il;
wyad wea eni
JEzELI INT TO @przerw GDY NIE @norm;
@norm czyt wys weja ode weak wyl wea KONIEC;
@przerw czyt wys weja ode weak dws;
wyls wes wyws wea;
pisz;
wyap wel wea rint;

// Reads value from Memory[AD] to AK
// Pobiera wartość z Pemięci[AD] do AK
ROZKAZ POB;
czyt wys wei il;
wyad wea eni
JEzELI INT TO @przerw GDY NIE @norm;
@norm czyt wys weja przep weak wyl wea KONIEC;
@przerw czyt wys weja przep weak dws;
wyls wes wyws wea;  //save (L)->stack
pisz;
wyap wel wea rint;

// Loads value from AK into the Memory[AD] 
// Ładuje wartość z AK do Pemięci[AD]
ROZKAZ LAD;
czyt wys wei il;
wyad wea wyak wes eni
JEzELI INT TO @przerw GDY NIE @norm;
@norm pisz wyl wea KONIEC;
@przerw pisz dws;
wyls wes wyws wea;
pisz;
wyap wel wea rint;

// Unconditional jump to AD addres 
// Skok bezwarunkowy do adresu AD
ROZKAZ SOB;
czyt wys wei il;
wyad wea wel;

// Jump when AK<0 to AD addres 
// Skok gdu AK<0 do adresu AD
ROZKAZ SOM;
czyt wys wei il;
JEZELI Z TO @negative GDY NIE @positive;
@negative wyad wea wel KONIEC;
@positive wyl wea;

// Jump when AK==0 to AD addres 
// Skok gdu AK==0 do adresu AD
ROZKAZ SOZ;
czyt wys wei il;
JEzELI zak TO @zero GDY NIE @notzero;
@zero wyad wea wel KONIEC;
@notzero wyl wea;

// read sign from external device
ROZKAZ IN;
czyt wys wei il;
start wyak weja ode weak;
@czekaj wyg weja ode weak JEzELI Z to @dalej GDY NIE @czekaj;
@dalej wyrb weja przep weak wyl wea;

ROZKAZ OUT;
czyt wys wei il;
wyak wes weja ode weak werb start eni;
@wait JEzELI INT TO @interrupt GDY NIE @norm;
@norm wyg weja ode weak JEzELI Z TO @done GDY NIE @wait;
@done wys weja przep weak wyl wea KONIEC;
@interrupt wys weja przep weak dws;	//
wyls wes wyws wea;           //  (L) -> stack
pisz;
wyap wel wea rint;     //  (AP)->L,A

rozkaz GOS; //give on stack;
argumenty 0;
czyt wys wei il;
dws;
wyws wea wyak wes;
pisz wyl wea;

rozkaz RFI; //return from the interrupt
argumenty 0;
czyt wys wei il;
wyws wea iws;
czyt wys as wea wel;

rozkaz TFS; // take from stack
argumenty 0;
czyt wys wei il;
wyws wea iws;
czyt wys weja przep weak wyl wea;

// Set Mask  (immediate addressing)
ROZKAZ MAS;
czyt wys wei il;
wyad werm;
wyl wea;

// Set Mask (direct addressing)
ROZKAZ MasOld;
czyt wys wei il;
wyad wea;
czyt wys as werm;
wyl wea;

// reading of the mask register
ROZKAZ RMAS;
czyt wys wei il;
wyad wea;
wyrm as wes;
pisz wyl wea;
